msg.payload.IOT_BOOL = msg.payload.data[0] >0 ? true : false;

var data = [];
data[0]=msg.payload.buffer[2];
data[1]=msg.payload.buffer[1];


// Create a buffer
var buf = new ArrayBuffer(4);
// Create a data view of it
var view = new DataView(buf);
/*
data.forEach(function (b, i) {
    view.setUint8(i, b);
});
*/
data.forEach((element, index) => view.setUint8(index, element));
msg.payload.IOT_INT = view.getInt16(0,true);

data[0]=msg.payload.buffer[5];
data[1]=msg.payload.buffer[4];
data[2]=msg.payload.buffer[7];
data[3]=msg.payload.buffer[6];
data.forEach(function (b, i) {
    view.setUint8(i, b);
});

// Read the bits as a float; note that by doing this, we're implicitly
// converting it from a 32-bit float into JavaScript's native 64-bit double
msg.payload.IOT_REAL = view.getFloat32(0,true);


data[0]=msg.payload.buffer[9];
data[1]=msg.payload.buffer[8];
data[2]=msg.payload.buffer[11];
data[3]=msg.payload.buffer[10];

data.forEach(function (b, i) {
    view.setUint8(i, b);
});

msg.payload.IOT_TIME= view.getInt32(0,true);
return msg;
